import React, { useCallback, useContext, useState } from 'react';
import { withRouter } from 'react-router';

import {connect} from "react-redux";

import  './Authentication.scss';
import Input from '../../atoms/Form/Input/Input'
import Button from '../../atoms/Button/Button';
import Spinner from '../../atoms/Spinner/Spinner'
import * as actions from '../../../store/actions'
import config from '../../../store/firebaseConfig';
import { AuthContext } from "../../../Login1/Auth";


const Authentication = ({ history }, props) => {
    console.log('auth props', props );

    const [loginForm, setLoginForm] = useState({
        email: {
            elType: 'input',
            elConfig: {
                type: 'email',
                placeholder: 'E-Mail-Adresse'
            },
            value: '',
            validation: {
                required: true,
                isEmail: true
            },
            valid: false,
            touched: false

        },
        password: {
            elType: 'input',
            elConfig: {
                type: 'password',
                placeholder: 'Passwort'
            },
            value: '',
            validation: {
                required: true,
                minLength: 6
            },

            valid: false,
            touched: false

        }
    });
    const [errorMessage, setErrorMessage] = useState(null);

    const checkValidation = (value, rules) => {
        let isValid = true;

        if (!rules) {
            return true;
        }

        if (rules.required) {
            isValid = value.trim() !== '' && isValid;
        }
        if (rules.minLength) {
            isValid = value.length >= rules.minLength && isValid
        }
        if (rules.maxLength) {
            isValid = value.length <= rules.maxLength && isValid
        }
        if (rules.isEmail) {
            const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
            isValid = pattern.test(value) && isValid
        }
        if (rules.isNumeric) {
            const pattern = /^\d+$/;
            isValid = pattern.test(value) && isValid
        }

        return isValid;
    };

    const inputChangedHandler = (ev, formId) => {
        console.log("inputChangedHandler value - formId", ev.target.value, formId);

        //touched, wenn user etwas eingegeben hat
        const updatedControls = {
            ...loginForm,
            [formId]: {
                ...loginForm[formId],
                value: ev.target.value,
                valid: checkValidation(ev.target.value, loginForm[formId].validation),
                touched: true
            }
        };
        console.log('updatedControls::',updatedControls);
        setLoginForm(updatedControls);
    };

    const onSubmitFormHandler = useCallback(async event => {
        event.preventDefault();
        console.log('history', history);
        let email = event.target[0].value;
        let password = event.target[1].value;
        try {
            await config
                .auth()
                .signInWithEmailAndPassword(email, password);
            history.push('/dashboard');
        } catch (error) {
            console.log("ERROR: ", error);

            if(!!email) {
                console.log('email leer 11');
            }

            if(!email.length) {
                console.log('email leer 22');
            }

            if(email) {
                console.log('email leer 33');
            }

            let errorMsg = <div className="ErrorMsg"><p>Email Adresse oder Passwort ist falsch!</p></div>;
            setErrorMessage(errorMsg);
        }
    }, [history]);

    //const { currentUser } = useContext(AuthContext);

    //console.log("currentUser ", currentUser);
    // if(currentUser) {
      //  return <Redirect to={"/"} />;
    // }


    const loginFormEl = [];
    for (let key in loginForm) {
        loginFormEl.push({
            id: key,
            config: loginForm[key]
        });
    }

    let form = loginFormEl.map(formEl => (
        <Input
            elType={formEl.config.elType}
            key={formEl.id}
            elConfig={formEl.config.elConfig}
            value={formEl.config.value}
            invalid={!formEl.config.valid}
            shouldValidate={formEl.config.validation}
            touched={formEl.config.touched}
            label={formEl.config.elConfig.placeholder}
            changed={(ev) => inputChangedHandler(ev, formEl.id)}
        />)
    );
/**
    if (props.loading) {
        form = <Spinner/>
    }

    if (props.error) {
        errorMessage = (
            <p>{props.error.message}</p>
        )
    }


    if (props.isAuthenticated) {
        authRedirect = <Redirect to="/"/>
    }
**/
    return (
        <div className="Auth">
            <h3>Login </h3>

            <form className="AuthForm" onSubmit={(ev) => onSubmitFormHandler(ev)}>
                {form}
                <Button
                    type="submit"
                    btnType="Primary"

                >Anmelden </Button>
            </form>
            {errorMessage}
        </div>
    );
}

/**
const mapStateToProps = state => {
    return {
        loading: state.auth.loading,
        error: state.auth.error,
        isSignIn: state.auth.isSignIn,
        isAuthenticated: state.auth.tokenId !== null
    };
};

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (email, password) => dispatch(actions.auth(email, password))
    };
};
**/

//export default connect(mapStateToProps, mapDispatchToProps)(Authentication);

export default withRouter(Authentication);
