import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import useAxios from 'axios-hooks';
import axios from '../../store/axios-instance';
import './Services.scss';

const Services = () => {

    const [ servicesInfo, setServicesInfo ] = useState([]);


    useEffect (async () => {

        document.title = "Container Logistik – ein Partner und alle Leistungen | Kaefer Logistik";

        const result = await axios.get('/services.json');

        console.log('===result.data===', result.data.services);

        setServicesInfo( result.data.services);
    }, []);


    return (
        <div className="Services">
            {
                <React.Fragment>
                    <h1 className="Headline">{ servicesInfo != null && servicesInfo.headline}</h1>

                    <div>
                        <h2 className="Subheadline">{ servicesInfo != null && servicesInfo.subheadline_1}</h2>
                        <ul>
                            {
                                servicesInfo.services_infos_1.map((value, key)=> (
                                    <li key={'service_info_2_'+key}> <FontAwesomeIcon icon={faCheck} transform="shrink-3" color="gray" /> <p>{value}</p></li>
                                ))

                            }

                        </ul>
                        <h2 className="Subheadline">{servicesInfo !== null && servicesInfo.subheadline_2} </h2>


                        <p className="Sentence-More"> und noch vieles mehr... </p>

                        <p><a  className="ContactLink"  href="/contact">Wir freuen uns auf Ihre Kontaktaufnahme.</a></p>
                    </div>
                </React.Fragment>
            }
        </div>
    );
};

export default Services;
