import React from 'react';

import ModalTrigger from "./ModalTrigger/ModalTrigger";
import ModalContent from "./ModalContent/ModalContent";

const  Modal = (props) => {

        this.state = {
            isShown: false
        };


    const showModal = () => {
        console.log('--showModal--');
        this.setState({ isShown: true }, () => {
            closeButton.focus();
        });
        toggleScrollLock();
    };
    
    const closeModal = () => {
        console.log('--closeModal--');
        this.setState({ isShown: false });
        TriggerButton.focus();
        toggleScrollLock();
    };
    
    const onKeyDown = event => {
        console.log('--onKeyDown--');
        if (event.keyCode === 27) {
            closeModal();
        }
    };
    
    const onClickOutside = event => {
        console.log('--onClickOutside--');
        if (this.modal && this.modal.contains(event.target)) return;
        closeModal();
    };
    
    const toggleScrollLock = () => {
        console.log('--toggleScrollLock--');
        document.querySelector('body').classList.toggle('scroll-lock');
    };

        console.log('==Modal== ' ,props.onClose, );

    return (
        <React.Fragment>
                <ModalTrigger
                    showModal={showModal}
                    buttonRef={ev => (this.TriggerButton = ev)}
                    triggerText={props.buttonText}
                    awesomeIcon={props.awesomeIcon}
                />
                {
                    this.state.isShown ? (
                        <ModalContent
                            modalRef={ev => (this.modal = ev)}
                            buttonRef={ev => (this.closeButton = ev)}
                            closeModal={closeModal}
                            content={props.modalContent}
                            onKeyDown={onKeyDown}
                            onClickOutside={onClickOutside}
                        />
                     ) : null

                }
        </React.Fragment>
    );

};

export default Modal;
