import React, { useCallback, useContext, useState, useEffect } from 'react';
import { Redirect } from 'react-router';

import {connect} from "react-redux";

import  './Authentication.scss';
import Input from '../../atoms/Form/Input/Input'
import Button from '../../atoms/Button/Button';
import Spinner from '../../atoms/Spinner/Spinner'
import * as actions from '../../../store/actions'
import config from '../../../store/firebaseConfig';
import { AuthContext } from "../../../Login1/Auth";


const Authentication = ({ history }) => {
    const [loginForm, setLoginForm] = useState({
            email: {
                elType: 'input',
                elConfig: {
                    type: 'email',
                    placeholder: 'E-Mail-Adresse'
                },
                value: '',
                validation: {
                    required: true,
                    isEmail: true
                },
                valid: false,
                touched: false

            },
            password: {
                elType: 'password',
                elConfig: {
                    type: 'password',
                    placeholder: 'Passwort'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 6
                },

                valid: false,
                touched: false

            }
    });

    useEffect(() => {

    },[]);

    const checkValidation = (value, rules) => {
        let isValid = true;
    
        if (!rules) {
            return true;
        }
    
        if(rules.required) {
            isValid = value.trim() !== '' && isValid;
        }
        if(rules.minLength) {
            isValid = value.length >= rules.minLength && isValid
        }
        if(rules.maxLength) {
            isValid = value.length <= rules.maxLength &&  isValid
        }
        if (rules.isEmail) {
            const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
            isValid = pattern.test(value) && isValid
        }
        if (rules.isNumeric) {
            const pattern = /^\d+$/;
            isValid = pattern.test(value) && isValid
        }
    
        return isValid;
    };
    
    const inputChangedHandler = (ev, formId) => {
        console.log("inputChangedHandler value - formId", ev.target.value, formId);
        
        //touched, wenn user etwas eingegeben hat
        const updatedControls = {
            ...loginForm,
            [formId]: {
                ...loginForm[formId],
                value: ev.target.value,
                valid: checkValidation(ev.target.value, loginForm[formId].validation),
                touched: true
            }
        };
        console.log("updatedControls:: ", updatedControls);

        // loginForm: updatedControls;
    };


    const onSubmitFormHandler = useCallback(async event => {
        event.preventDefault();
        const { email, password } = event.target.elements;
        try {
            await config
                .auth()
                .signInWithEmailAndPassword(email.value, password.value);
            history.push('/dashboard');
        } catch (error) {
            console.log("ERROR LOGIN: ", error);
        }
    },[history] );

   // const { currentUser } = useContext(AuthContext);

   // if(currentUser) {
    //    return <Redirect to={"/"} />;
   // }


    const loginFormEl = [];
    let errorMessage = null;
        
    for (let key in loginForm) {
        loginFormEl.push({
            id: key,
            config: loginForm[key]
        });
    }
        
        
    let form = loginFormEl.map( formEl => (
        <Input
            elType={formEl.config.elType}
            key={formEl.id}
            elConfig={formEl.elConfig}
            value={formEl.config.value}
            invalid={!formEl.config.valid}
            shouldValidate={formEl.config.validation}
            touched={formEl.config.touched}
            label={formEl.config.elConfig.placeholder}
            changed={(ev) => inputChangedHandler(ev, formEl.id)}
        /> )
    );
        
   // if(this.props.loading) {
    //    form = <Spinner/>
    //}

  //if(this.props.error){
    //   errorMessage = (
     //      <p>{this.props.error.message}</p>
      // )
  // }
       
   let authRedirect = null;
  // if (this.props.isAuthenticated) {
    //    authRedirect = <Redirect to="/"/>
    //}
      
    return (
        <div className="Auth">
            {authRedirect}
            <h3>Login </h3>
            {errorMessage}
            <form  onSubmit={(ev) => onSubmitFormHandler(ev)}>
                {form}
                <Button
                    type="submit"
                    btnType="Primary"
                >Anmelden </Button>
            </form>

        </div>
    );

};


export default Authentication;
